//创建新的axios对象
// 创建新的 axios 对象
import axios from 'axios'
const token = localStorage.getItem("token");
const _axios = axios.create({
  baseURL: 'http://localhost:7078',
  headers: {
    // 如果有存储JWT令牌的地方（例如Vuex），可以从那里获取令牌
    'Authorization': token,
  },
})



export default _axios
